import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';

const mapkey = "AIzaSyA5XxXKw44uRve7wAs046c_jGmKNhK6B1Y";
var startingLat = 43.59;
var startingLng = -79.70;
const defaultZoom = 8;

class Map extends Component {
  constructor(){
		super();
		this.state = {lat: startingLat, lng: startingLng}
		this.handleClick = this.handleClick.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleZoomChange = this.handleZoomChange.bind(this);
	}
	
	handleClick(e){
		this.setState({lat: e.lat, lng: e.lng});
	}
	
	handleSubmit(){
		this.props.submit(this.state.lat, this.state.lng);
	}
	
	handleZoomChange(e){
		console.log(e)
	}
	
  render() {
    return (
      <div id="mapContainer">
				<label>Or click somewhere on the map to see the forecast there</label> <br></br>
				<label>Latitude: {this.state.lat} Longitude: {this.state.lng}</label>
				<br></br>
				<input type="submit" value="Submit Coordinates" onClick={this.handleSubmit}/>
        <GoogleMapReact
          bootstrapURLKeys={{ key: mapkey}}
          defaultCenter={{lat: startingLat, lng: startingLng}}
          defaultZoom={defaultZoom}
					onClick={this}
					onZoomChange={() => console.log("bitch")}
        >
        </GoogleMapReact>
      </div>
    );
  }
	
	componentWillUnmount() {
			startingLat = this.state.lat;
			startingLng = this.state.lng;
	}
}

export default Map;
