import React from 'react';

class DayCast extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			values: 0,
			day: "",
			start: 0
		}
	}
	
	static getDerivedStateFromProps(props,state){
		return ({day: props.day, values: props.values, start: props.start});
	}
	
	/*async componentDidMount(props){
		this.setState({values: await Data.getHourly(this.state.day)});
	}*/
	
	render(){
