import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';

const mapkey = "AIzaSyA5XxXKw44uRve7wAs046c_jGmKNhK6B1Y";
const defaultLat = 43.59;
const defaultLng = -79.70;
const defaultZoom = 8;

class Map extends Component {
  constructor(){
		super();
		this.state = {lat: defaultLat, defaultLng: -79.70}
		this.handleClick = this.handleClick.bind(this);
	}
	
	handleClick(e){
		this.setState({lat: e.lat, lng: e.lng});
		this.props.click(e.lat,  e.lng);
	}
	
  render() {
    return (
      <div id="mapContainer">
				<label>Or click somewhere on the map to see the forecast there</label>
        <GoogleMapReact
          bootstrapURLKeys={{ key: mapkey}}
          defaultCenter={{lat: defaultLat, lng: defaultLng}}
          defaultZoom={defaultZoom}
					onClick={this.handleClick}
        >
        </GoogleMapReact>
      </div>
    );
  }
}

export default Map;
