import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';

const mapkey = "AIzaSyA5XxXKw44uRve7wAs046c_jGmKNhK6B1Y";

class Map extends Component {
  constructor(){
		super();
		this.state = {lat: 43.59, lng: -79.70, zoom: 8}
		this.handleClick = this.handleClick.bind(this);
	}
	
	handleClick(e){
		//this.setState({lat: e.lat, lng: e.lng});
		//this.props.click(e.lat,  e.lng);
		console.log(e.lat);
	}
	
  render() {
    return (
      <div id="mapContainer">
				<label>Or click somewhere on the map to see the forecast there</label>
        <GoogleMapReact
          bootstrapURLKeys={{ key: mapkey}}
          defaultCenter={{lat: this.state.lat, lng: this.state.lng}}
          defaultZoom={this.state.zoom}
					onClick={this.handleClick}
        >
        </GoogleMapReact>
      </div>
    );
  }
}

export default Map;
