import React from 'react';
import WeatherCard from './WeatherCard.js';
import CityList from './CityList.js';

const key = "54a84a123d401ac68736a6bca89f4301";
const ids = {6167865: "Toronto", 5907364: "Brampton", 6075357: "Mississauga",
6122091: "Richmond Hill", 5992996: "Kitchener", 6176823: "Waterloo",
6066513: "Markham", 6094817: "Ottawa", 5969782: "Hamilton"
, 6173577: "Vaughan", 6094578: "Oshawa"};
const codes = {toronto: '6167865', brampton: '5907364',
		mississauga: '6075357', richmondhill: '6122091', kitchener: '5992996',
		waterloo: '6176823', markham: '6066513', ottawa: '6094817',
		hamilton: '5969782', vaughan: '6173577', oshawa: '6094578'};
const url = "http://api.openweathermap.org/data/2.5/forecast?id=6167865" + "&appid=" + key;
const divStyle = {
	width: "20%",
	float: "left"
};

class Forecast extends React.Component {
	constructor(props) {
		super(props);
		this.state = {days: 0,
		highs: 0,
		lows: 0,
		conds: 0,
		city: 'toronto'
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		this.setState({city: event.target.value});
	}

	handleSubmit(event) {

    event.preventDefault();
  }

	render(){
		if (this.state.city === 'brampton'){
			return (<h1>{"The Forecast is loading, please wait"}</h1>)
		}
		else {
			return (
			<div>
			<div>
			</div>
			<form onSubmit={this.handleSubmit}>
        <label>
          Choose a city to display its forecast <br></br>
          <select value={this.state.city} onChange={this.handleChange}>
            <option value="toronto">Toronto</option>
            <option value="brampton">Brampton</option>
						<option value="mississauga">Mississauga</option>
						<option value="richmondhill">Richmond Hill</option>
						<option value="kitchener">Kitchener</option>
						<option value="waterloo">Waterloo</option>
						<option value="markham">Markham</option>
						<option value="ottawa">Ottawa</option>
						<option value="hamilton">Hamilton</option>
						<option value="vaughan">Vaughan</option>
						<option value="oshawa">Oshawa</option>
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
			</div>
			);
		}
	}

	/*async componentDidMount(){
		const info = await Data.getEverything();
		this.setState({days: info.days, highs: info.highs,
		lows: info.lows, conds: info.conditions});
	}*/
}

export default Forecast;
